const out = [];
for (const item of $input.all()) {
  const r = item.json;

  // Clean numeric fields (strip commas etc then convert)
  const num = v => Number(String(v).replace(/,/g, ''));

  out.push({
    json: {
      date: r.date,                      // YYYY-MM-DD
      platform: r.platform,
      account: r.account,
      campaign: r.campaign,
      country: r.country,
      device: r.device,
      spend: num(r.spend),
      clicks: num(r.clicks),
      impressions: num(r.impressions),
      conversions: num(r.conversions),
      load_date: r.load_date,            // from Set Metadata
      source_file_name: r.source_file_name
    }
  });
}
return out;
